			}
			if (!result.HasValue && containerProperty != null)
			{
				result = containerProperty!.ItemIsReference;
			}
			if (!result.HasValue && collectionContract != null)
			{
				result = collectionContract!.ItemIsReference;
			}
			if (!result.HasValue)
